mapper.update:
UPDATE ext_startup_logs
set province = #{0}
WHERE id = #{1};

mapper.select:
SELECT count(*) FROM ( SELECT deviceId FROM ext_startup_logs WHERE appId = #{appId} AND appVersion = #{version} AND FROM_UNIXTIME( createdatms / 1000, "%Y/%m/%d" ) = date_format(DATE_ADD(#{time} , INTERVAL #{dayNum,jdbcType=INTEGER} DAY),"%Y/%m/%d") GROUP BY deviceId HAVING
#{id}
)t

        SELECT COUNT(*) num,deviceStyle
from(
SELECT deviceStyle
FROM ext_startup_logs
where appId = #{appId}
GROUP BY deviceId
)t
GROUP BY deviceStyle
ORDER BY num desc
LIMIT 6;

        SELECT count(*) num,network,appVersion
from(
SELECT DISTINCT deviceId,network,appVersion
FROM ext_startup_logs
where appId = #{appId}
)t
GROUP BY network,appVersion

SELECT COUNT(*) num,appVersion
FROM(
select appVersion
from ext_startup_logs
WHERE appId = #{appId} and deviceStyle = #{deviceStyle}
GROUP BY deviceId
)t
GROUP BY appVersion;
